package com.demo.jetpackcomposebasicimport android.app.Dialogimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.heightimport androidx.compose.material.AlertDialogimport androidx.compose.material.OutlinedButtonimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp@Composablefun Dialog(    showDialog: Boolean,    dismissDialog: () -> Unit) {    if (showDialog) {        AlertDialog(            onDismissRequest = {},            title = {                Text(                    text = "DialogÂ´s title",                    style = TextStyle(fontSize = 22.sp, fontWeight = FontWeight.Bold)                )            },            text = {                Column {                    Text(text = "Row One")                    Spacer(modifier = Modifier.height(4.dp))                    Text(text = "Row Two")                    Spacer(modifier = Modifier.height(4.dp))                    Text(text = "Row Three")                }            },            confirmButton = {                OutlinedButton(onClick = {                    dismissDialog()                }) {                    Text(text = "OK")                }            },            dismissButton = {                OutlinedButton(onClick = {                    dismissDialog()                }) {                    Text(text = "Cancel")                }            }        )    }}