package com.demo.jetpackcomposebasicimport androidx.compose.foundation.layout.*import androidx.compose.material.Buttonimport androidx.compose.material.OutlinedTextFieldimport androidx.compose.material.Textimport androidx.compose.material.TextFieldimport androidx.compose.runtime.*import androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp@Composablefun ScreenOne(    goToScreenTwo: (String) -> Unit) {    var textValue by remember {        mutableStateOf("")    }    Column(        modifier = Modifier            .fillMaxSize()            .padding(16.dp),        verticalArrangement = Arrangement.SpaceAround,        horizontalAlignment = Alignment.CenterHorizontally    ) {        Text(            text = "SCREEN 1",            style = TextStyle(color = Color.Blue, fontSize = 42.sp, fontWeight = FontWeight.Bold)        )        OutlinedTextField(            value = textValue,            onValueChange = { textValue = it },            label = { Text(text = "Introduce un texto") }        )        Button(onClick = { goToScreenTwo(textValue.ifEmpty { "Write first" }) }) {            Text(text = "Go to Screen Two")        }    }}