package com.demo.jetpackcomposebasicimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.OutlinedButtonimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp@Composablefun ScreenOne() {    val openDialog = remember { mutableStateOf(false) }    Column(        modifier = Modifier            .fillMaxSize()            .padding(16.dp),        verticalArrangement = Arrangement.SpaceAround,        horizontalAlignment = Alignment.CenterHorizontally    ) {        Text(            text = "SCREEN ONE",            style = TextStyle(color = Color.Green, fontSize = 42.sp, fontWeight = FontWeight.Bold)        )        OutlinedButton(onClick = { openDialog.value = true }) {            Text(text = "Continue")        }    }    Dialog(showDialog = openDialog.value, dismissDialog = { openDialog.value = false })}