package com.demo.jetpackcomposebasicimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material.Buttonimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp@Composablefun ScreenTwo(    text : String,    gotToBack: () -> Unit) {    Column(        modifier = Modifier            .fillMaxSize()            .padding(16.dp),        verticalArrangement = Arrangement.SpaceAround,        horizontalAlignment = Alignment.CenterHorizontally    ) {        Text(            text = "SCREEN 2",            style = TextStyle(color = Color.Red, fontSize = 42.sp, fontWeight = FontWeight.Bold)        )        Text(            text = text,            style = TextStyle(color = Color.Red, fontSize = 42.sp, fontWeight = FontWeight.Bold)        )        Button(onClick = {            gotToBack()        }) {            Text(text = "back to Screen One")        }    }}