package com.demo.jetpackcomposebasic.navigationimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.Favoriteimport androidx.compose.material.icons.filled.Homeimport androidx.compose.material.icons.filled.Settingsimport androidx.compose.ui.graphics.vector.ImageVectorsealed class Destination(    val route: String,    val title: String,    val icon: ImageVector) {    object Screen1 : Destination("screen1", "Screen One", Icons.Filled.Home)    object Screen2 : Destination("screen2/?newText={newText}", "Screen Two", Icons.Filled.Settings) {        fun createRoute(newText: String) = "screen2/?newText=$newText"    }    object Screen3 : Destination("screen3", "Screen Three", Icons.Filled.Favorite)}