package com.demo.jetpackcomposebasic.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.NavControllerimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport com.demo.jetpackcomposebasic.ScreenOneimport com.demo.jetpackcomposebasic.ScreenTwoimport com.demo.jetpackcomposebasic.presentation.screens.ScreenThree@Composablefun NavigationHost(    navController: NavHostController) {    NavHost(navController = navController, startDestination = Destination.Screen1.route) {        composable(Destination.Screen1.route) {            ScreenOne(goToScreenTwo = { newText ->                navController.navigate(Destination.Screen2.createRoute(newText))            })        }        composable(Destination.Screen2.route) { navBackStackEntry ->            val newText = navBackStackEntry.arguments?.getString("newText")            ScreenTwo(                text = newText.toString(),                gotToBack = { navController.popBackStack() })        }        composable(Destination.Screen3.route) { navBackStackEntry ->            val newText = navBackStackEntry.arguments?.getString("newText")            ScreenThree(                text = newText.toString(),                gotToBack = { navController.popBackStack() })        }    }}